#include<stdlib.h>
#include"stack.h"
STACK*createStack(void){STACK*stack;stack=(STACK*)malloc(sizeof(STACK));if(stack){stack->top=NULL;stack->count=0;}return stack;}int pushStack(STACK*stack,void*dataInPtr){STACK_NODE*newPtr;newPtr=(STACK_NODE*)malloc(sizeof(STACK_NODE));if(!newPtr)return 0;newPtr->dataPtr=dataInPtr;newPtr->link=stack->top;stack->top=newPtr;(stack->count)++;return 1;}void*popStack(STACK*stack){void*dataOutPtr;STACK_NODE*temp;if(stack->count==0)dataOutPtr=NULL;else{temp=stack->top;dataOutPtr=stack->top->dataPtr;stack->top=stack->top->link;free(temp);(stack->count)--;}return dataOutPtr;}void*stackTop(STACK*stack){if(stack->count > 0)return stack->top->dataPtr;else return NULL;}int emptyStack(STACK*stack){return(stack->count==0);}int fullStack(STACK*stack){STACK_NODE*temp;if((temp=(STACK_NODE*)malloc(sizeof(STACK_NODE)))){free(temp);return 0;}return 1;}int stackCount(STACK*stack){return stack->count;}STACK*destroyStack(STACK*stack){STACK_NODE*temp;if(stack){while(stack->top !=NULL){free(stack->top->dataPtr);temp=stack->top;stack->top=stack->top->link;free(temp);}free(stack);}return NULL;}
